    "text": "/*\
title: $:/plugins/Telumire/Animate/scroll-direction.js
type: application/javascript
module-type: startup

Detects the direction of the vertical scroll and sets $:/temp/animate/scroll-direction to either "up" or "down".

\*/
(function () {

	exports.name = "scroll-detection";

	exports.synchronous = true;

	exports.startup = function () {
		const SCROLL_DIRECTION_TIDDLER = "$:/temp/animate/scroll-direction";
		const MIN_SCROLL_POSITION = 0;
		let lastKnownScrollPosition = 0;
		let lastDirection = 0;
		// Setup a timer
		let timeoutId;
		const handleScroll = () => {
			if (timeoutId) {
			window.cancelAnimationFrame(timeoutId);
			}

			timeoutId = window.requestAnimationFrame(() => {
				let previousKnownScrollPosition = lastKnownScrollPosition;
				lastKnownScrollPosition = window.pageYOffset;

				let direction = lastKnownScrollPosition > previousKnownScrollPosition;

				if (direction !== lastDirection && lastKnownScrollPosition > MIN_SCROLL_POSITION) {
					$tw.wiki.setText(SCROLL_DIRECTION_TIDDLER, "text", null, direction ? "down" : "up");
					lastDirection = direction;
				}

			});
		}

		try {
			window.addEventListener("scroll", handleScroll, false);
		} catch (e) {
			console.error("Failed to add scroll event listener:", e);
		}
	};
})();",
