"$:/plugins/Telumire/Commons/macros/openControlPanelTab": {
    "created": "20230424221125460",
    "text": "\define link-to-controlpanel-tab-actions()

<$action-setfield $tiddler="$:/state/tab-1749438307" text=<<main-tab>> />
<$action-setfield $tiddler="$:/state/tab--1963855381" text=<<sub-tab>> />
<$action-navigate $to=<<navigateTo>>/>

\end

\define link-to-controlpanel-tab-separator()<$list filter="[<depth-last>!match[yes]]"><$text text=<<__separator__>>/></$list>

\define link-to-controlpanel-tab-template(separator) <$tiddler tiddler=<<navigateTo>> ><$button tag="a" actions=<<link-to-controlpanel-tab-actions>> class="tc-tiddlylink tc-tiddlylink-shadow"><$transclude field="caption"><$transclude field="title"/></$transclude></$button></$tiddler><<link-to-controlpanel-tab-separator>>

\define link-to-controlpanel-tab(caption:"""<code><$list filter="$:/ControlPanel [<main-tab>] [<sub-tab>]" counter="depth" variable="navigateTo"><<link-to-controlpanel-tab-template " > ">></$list></code>""",prefix,sub-tab)
<$let
prefix={{{ [<__prefix__>!is[blank]]:else[{!!title}split[/]first[4]join[/]] }}} 
sub-tab={{{ [<__sub-tab__>!is[blank]]:else[all[tiddlers+shadows]prefix<prefix>search:tags[$:/tags/ControlPanel]] }}}
main-tab={{{ [<sub-tab>tags[]prefix[$:/tags/ControlPanel/]trim[$:/tags/]] :map[[$:/tags/ControlPanel]tagging[]search{!!title}] }}}
tv-wikilinks="no"
><<__caption__>></$let>
\end",
